import React, { useState, useEffect } from 'react';
import { Container, Grid, Typography, Card, CardContent, CardMedia, Button, TextField, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

// Tipo para los doctores
interface Doctor {
  id: number;
  name: string;
  specialty: string;
  rating: number;
  imageUrl: string;
}

const Home = () => {
  const [doctors, setDoctors] = useState<Doctor[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Simulación de datos - en producción se obtendría de la API
    const mockDoctors = [
      { id: 1, name: 'Dr. Juan Pérez', specialty: 'Cardiología', rating: 4.8, imageUrl: 'https://via.placeholder.com/150' },
      { id: 2, name: 'Dra. María López', specialty: 'Pediatría', rating: 4.9, imageUrl: 'https://via.placeholder.com/150' },
      { id: 3, name: 'Dr. Carlos Rodríguez', specialty: 'Neurología', rating: 4.7, imageUrl: 'https://via.placeholder.com/150' },
    ];
    setDoctors(mockDoctors);
  }, []);

  const handleDoctorClick = (id: number) => {
    navigate(`/doctors/${id}`);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Encuentra al mejor doctor
        </Typography>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Buscar por nombre o especialidad"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          sx={{ mb: 2 }}
        />
      </Box>

      <Typography variant="h5" component="h2" gutterBottom>
        Doctores destacados
      </Typography>

      <Grid container spacing={3}>
        {doctors.map((doctor) => (
          <Grid item xs={12} sm={6} md={4} key={doctor.id}>
            <Card sx={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
              <CardMedia
                component="img"
                height="140"
                image={doctor.imageUrl}
                alt={doctor.name}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography gutterBottom variant="h6" component="div">
                  {doctor.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Especialidad: {doctor.specialty}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Calificación: {doctor.rating}/5
                </Typography>
                <Button 
                  variant="contained" 
                  sx={{ mt: 2 }}
                  onClick={() => handleDoctorClick(doctor.id)}
                >
                  Ver perfil
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Home;
